import React from 'react'
import { Image, Tag, Spin } from 'antd';
import { Link } from 'umi'
import './productItem.less';
interface productItemType {
    item: any
}
const productItem: React.FC<productItemType> = (props) => {
    let tagsDom = props.item.tag.split(',').map((item: string, index: number) => {
        if (!item) {
            return ''
        }
        return (<Tag key={index} className="product-tag-item">{item}</Tag>)
    })

    if (!props.item) {
        return (<div className='product-item'>
            <div className="inner-container">
                <Spin></Spin>
            </div>
        </div>)
    }

    return (
        <div className='product-item'>
            <div className="inner-container">
                <div className="product-img-box">
                    <Link to={'/productdetails/' + props.item.id} className="product-link">
                        <Image
                            className="product-img"
                            src={'/api/download/img/' + props.item.adImgSrc}
                            fallback="data:image/png;base64,"
                        /></Link>
                </div>
                <div className="product-text-box">
                    <div className="price-box">
                        <span className="now-price">
                            {props.item.nowPrice}
                        </span>
                        <span className='original-price'>
                            {props.item.originalPrice}
                        </span>
                    </div>
                    <div className="product-name-box">
                        <Link className='product-name' to={'/productdetails/' + props.item.id}>{props.item.productName}</Link>
                    </div>
                    <div className="tags-box">
                        {tagsDom}
                    </div>
                </div>
            </div>
        </div>
    )
}

export default productItem